import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import socket from '../socket';

export default function Home() {
  const [anonymous, setAnonymous] = useState(true);
  const [name, setName] = useState('');
  const [lobbyMode, setLobbyMode] = useState(null); // 'create' | 'join' | null
  const [lobbyCode, setLobbyCode] = useState('');
  const [inputCode, setInputCode] = useState('');
  const [waiting, setWaiting] = useState(false);
  const [error, setError] = useState('');
  const [lobbyReady, setLobbyReady] = useState(false);
  const [isCreator, setIsCreator] = useState(false);
  const navigate = useNavigate();

  const handleStart = () => {
    navigate('/match', { state: { anonymous } });
  };

  const handleCreateLobby = () => {
    setError('');
    setLobbyMode('create');
    setWaiting(true);
    setIsCreator(true);
    socket.emit('create_lobby', { name, anonymous }, ({ code }) => {
      setLobbyCode(code);
      setWaiting(false);
    });
    socket.on('lobby_ready', () => {
      setLobbyReady(true);
    });
  };

  const handleJoinLobby = () => {
    setError('');
    setLobbyMode('join');
    setIsCreator(false);
  };

  const handleJoinSubmit = () => {
    setError('');
    setWaiting(true);
    socket.emit('join_lobby', { code: inputCode.trim().toUpperCase(), userData: { name, anonymous } }, (res) => {
      setWaiting(false);
      if (res && res.error) setError(res.error);
      else setLobbyCode(inputCode.trim().toUpperCase());
    });
    socket.on('lobby_ready', () => {
      setLobbyReady(true);
    });
  };

  const handleStartLobbyGame = () => {
    socket.emit('start_lobby_game', { code: lobbyCode });
  };

  // Clean up listeners on unmount
  React.useEffect(() => {
    const onGameStarted = ({ questions, gameId }) => {
      setLobbyMode(null);
      setLobbyCode('');
      setLobbyReady(false);
      navigate('/questions', { state: { anonymous, name, questions, gameId } });
    };
    socket.on('lobby_game_started', onGameStarted);
    return () => {
      socket.off('lobby_ready');
      socket.off('lobby_game_started', onGameStarted);
    };
  }, [navigate, name, anonymous]);

  return (
    <div className="min-h-screen flex items-center justify-center" style={{
      background: 'linear-gradient(135deg, #e664fa 0%, #7cf6fd 100%)',
    }}>
      <div className="bg-white rounded-3xl shadow-xl p-10 flex flex-col items-center max-w-md w-full">
        <div className="text-4xl mb-2">🎮</div>
        <h1 className="text-3xl font-bold mb-3 text-center">1v1 Compatibility Game</h1>
        <p className="text-lg text-gray-700 mb-6 text-center">
          Find your perfect match by answering 5 fun questions!
        </p>
        <div className="flex items-center mb-6">
          <input
            id="anonymous"
            type="checkbox"
            checked={anonymous}
            onChange={() => setAnonymous(!anonymous)}
            className="w-5 h-5 mr-2 accent-black"
          />
          <label htmlFor="anonymous" className="text-lg">Anonymous</label>
        </div>
        {!anonymous && (
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            className="mb-4 px-2 py-1 border rounded w-full text-center"
          />
        )}
        {!lobbyMode && (
          <div className="flex gap-4 mb-8 w-full justify-center">
            <button
              onClick={handleStart}
              className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-3 px-6 rounded-xl text-lg w-40 shadow-md transition"
            >
              Quick Match
            </button>
            <button
              className="bg-pink-500 hover:bg-pink-600 text-white font-bold py-3 px-6 rounded-xl text-lg w-40 shadow-md transition"
            >
              Create Lobby
            </button>
            <button
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-xl text-lg w-40 shadow-md transition"
            >
              Join Lobby
            </button>
          </div>
        )}
        {lobbyMode === 'create' && (
          <div className="mt-6 flex flex-col items-center w-full">
            <div className="text-base mb-2">Your Lobby Code:</div>
            <div className="text-xl font-mono font-bold text-blue-700 mb-2">{lobbyCode || '...'}</div>
            {!lobbyReady && <div className="text-sm text-gray-500 mb-2">Waiting for another player to join...</div>}
            {lobbyReady && isCreator && <button onClick={handleStartLobbyGame} className="w-full py-2 bg-blue-600 text-white rounded">Start Now</button>}
          </div>
        )}
        {lobbyMode === 'join' && (
          <div className="mt-6 flex flex-col items-center w-full">
            <input
              type="text"
              placeholder="Enter Lobby Code"
              value={inputCode}
              onChange={e => setInputCode(e.target.value)}
              className="text-lg px-2 py-1 border rounded w-full text-center mb-2"
            />
            <button onClick={handleJoinSubmit} className="w-full py-2 bg-blue-500 text-white rounded mb-2">Join</button>
            {lobbyCode && <div className="text-base text-gray-700 mb-2">Joined lobby <b>{lobbyCode}</b>. Waiting for another player...</div>}
          </div>
        )}
        {waiting && <div className="text-sm text-gray-500 mt-2">Please wait...</div>}
        {error && <div className="text-red-500 mt-2">{error}</div>}
        <div className="flex items-center mt-2">
          <span className="inline-block w-5 h-5 bg-green-500 rounded mr-2 flex items-center justify-center">
            <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" /></svg>
          </span>
          <span className="text-base font-medium">Backend &amp; Database Connected</span>
        </div>
      </div>
    </div>
  );
}
