import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import socket from '../socket';

export default function Home() {
  const [anonymous, setAnonymous] = useState(true);
  const [name, setName] = useState('');
  const [lobbyMode, setLobbyMode] = useState(null);
  const [lobbyCode, setLobbyCode] = useState('');
  const [inputCode, setInputCode] = useState('');
  const [waiting, setWaiting] = useState(false);
  const [error, setError] = useState('');
  const [lobbyReady, setLobbyReady] = useState(false);
  const [isCreator, setIsCreator] = useState(false);
  const navigate = useNavigate();

  const handleStart = () => {
    navigate('/match', { state: { anonymous, name } });
  };

  const handleCreateLobby = () => {
    setError('');
    setLobbyMode('create');
    setWaiting(true);
    setIsCreator(true);
    socket.emit('create_lobby', { name, anonymous }, ({ code }) => {
      setLobbyCode(code);
      setWaiting(false);
    });
    socket.on('lobby_ready', () => {
      setLobbyReady(true);
    });
  };

  const handleJoinLobby = () => {
    setError('');
    setLobbyMode('join');
    setIsCreator(false);
  };

  const handleJoinSubmit = () => {
    setError('');
    setWaiting(true);
    socket.emit('join_lobby', { code: inputCode.trim().toUpperCase(), userData: { name, anonymous } }, (res) => {
      setWaiting(false);
      if (res && res.error) setError(res.error);
      else setLobbyCode(inputCode.trim().toUpperCase());
    });
    socket.on('lobby_ready', () => {
      setLobbyReady(true);
    });
  };

  const handleStartLobbyGame = () => {
    socket.emit('start_lobby_game', { code: lobbyCode });
  };

  useEffect(() => {
    const onGameStarted = ({ questions, gameId }) => {
      setLobbyMode(null);
      setLobbyCode('');
      setLobbyReady(false);
      navigate('/questions', { state: { anonymous, name, questions, gameId } });
    };
    socket.on('lobby_game_started', onGameStarted);
    return () => {
      socket.off('lobby_ready');
      socket.off('lobby_game_started', onGameStarted);
    };
  }, [navigate, name, anonymous]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 via-white to-blue-200 px-4 py-10">
      <div className="max-w-md w-full bg-white shadow-xl rounded-2xl p-8 transition-all">
        <h1 className="text-3xl font-bold text-center text-blue-800 mb-2">ðŸŽ¯ 1v1 Compatibility Game</h1>
        <p className="text-center text-gray-600 text-sm mb-6">
          Match with someone and answer 5 fun questions to check your compatibility!
        </p>

        <div className="flex items-center justify-center gap-3 mb-4">
          <label className="text-sm font-medium text-gray-700">Anonymous</label>
          <input
            type="checkbox"
            checked={anonymous}
            onChange={() => setAnonymous(!anonymous)}
            className="w-4 h-4 accent-blue-500"
          />
        </div>

        {!anonymous && (
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="mb-4 w-full px-3 py-2 border rounded-md text-center focus:outline-none focus:ring-2 focus:ring-blue-300"
          />
        )}

        {!lobbyMode && (
          <div className="flex flex-col gap-3">
            <button onClick={handleStart} className="bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md font-semibold transition-all">
              Quick Match
            </button>
            <button onClick={handleCreateLobby} className="bg-green-600 hover:bg-green-700 text-white py-2 rounded-md font-semibold transition-all">
              Create Lobby
            </button>
            <button onClick={handleJoinLobby} className="bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-md font-semibold transition-all">
              Join Lobby
            </button>
          </div>
        )}

        {lobbyMode === 'create' && (
          <div className="mt-6 text-center space-y-2">
            <p className="text-gray-700">Your Lobby Code:</p>
            <p className="text-xl font-mono font-bold text-blue-700">{lobbyCode || '...'}</p>
            {!lobbyReady && <p className="text-sm text-gray-500">Waiting for another player to join...</p>}
            {lobbyReady && isCreator && (
              <button onClick={handleStartLobbyGame} className="mt-3 bg-blue-700 hover:bg-blue-800 text-white py-2 px-6 rounded-md font-semibold transition-all">
                Start Now
              </button>
            )}
          </div>
        )}

        {lobbyMode === 'join' && (
          <div className="mt-6 space-y-3">
            <input
              type="text"
              placeholder="Enter Lobby Code"
              value={inputCode}
              onChange={(e) => setInputCode(e.target.value)}
              className="w-full px-3 py-2 border rounded-md text-center uppercase tracking-wider focus:outline-none focus:ring-2 focus:ring-blue-300"
            />
            <button onClick={handleJoinSubmit} className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md font-semibold transition-all">
              Join
            </button>
            {lobbyCode && (
              <p className="text-sm text-gray-700 text-center">
                Joined lobby <strong>{lobbyCode}</strong>. Waiting for another player...
              </p>
            )}
          </div>
        )}

        {waiting && <p className="text-sm text-center text-gray-500 mt-4">Please wait...</p>}
        {error && <p className="text-sm text-center text-red-600 mt-4">{error}</p>}
      </div>
    </div>
  );
}
